function checkSiteLoad(){
	if(_loadCount == 2){

		_siteLoaded = true;

		spinner.stop(false);

		$('#home-page #fingers').animate({
			opacity:1
		}, 1000);

		$('#home-page #labels').animate({
			opacity:1
		}, 250);
	}
}


var _animateFingers = true;
var _rolledOverIndex = -1;
var _clicked = false;
var BAR_MIN = 50;
function animateFingers(){
	if(_animateFingers){
		$('.finger-move').each(function(index){
			if(index != _rolledOverIndex){
				var rand = Math.round(Math.random() * 150 + BAR_MIN);
				var yPos = parseInt($(this).css('top').replace('px', ''), 10);
				var distance = Math.abs(yPos - rand);
				var ratio = distance / 160;
				var animTime = (distance / 160) * 6000;

				$(this).stop().animate({
					top:rand
				}, animTime);

			}else{
				$(this).stop().animate({
					top:BAR_MIN
				}, 250);
			}
		});

		setTimeout(animateFingers, 500);
	}
}

function onFingerClick(index){
	if(_siteLoaded){
		_clicked = true;
		_animateFingers = false;

		$('#home-page #labels').animate({
			opacity:0
		}, 200);

		$('#home-page #text_area').animate({
			opacity:0
		}, 200);

		window.onNavClick(index, true);
	}
}

function onFingerOver(i){
	if(_siteLoaded){
		_rolledOverIndex = i;
		$('.finger-move:eq(' + i + ')').stop().animate({
			top:BAR_MIN
		}, 250);
		goMusicWord(i+1);

		sm.playMouseOver(i);
	}

}

function onFingerOut(index){
	if(_siteLoaded){
		_rolledOverIndex = -1;
		goMusicWord(0);
	}

}

