function createYouTubePlayer(videoIndex) {
	yp = new YouTubePlayer(videoIndex, 'section');
}

function destroyYouTubePlayer() {
	if(yp != undefined) {
		yp.destroy();
		yp = undefined;
	}
}

function onYouTubeEnded() {
	yp.destroy();
}

function onYouTubePlayerReady(playerId) {
	yp.onPlayerReady();
}

function replayYouTubeVideo() {
	yp.replayVideo();
}

function stopYouTubeVideo() {
	if(yp != undefined) {
		yp.stop();
	}
}

function hideYouTubeTitleCard() {
	if(yp != undefined) {
		yp.hideTitleCard();
	}
}

function onYouTubeStateChange(state) {
	switch(state) {
		case 0: // Ended
			yp.onPlayerEnded();
			break;
		case 1: // Play
			yp.onPlayStarted();
			break;
		case 2: // Paused
		case 3: // Buffering
		case 5: // Video cued
		case -1: // Unstarted
	}
}

function toggleVolume() {
	if(sm != undefined) {
		if(sm.isMuted) {
			$('#sound').attr('src', 'img/sound_on.png');
			sm.unmute();
		} else {
			$('#sound').attr('src', 'img/sound_off.png');
			sm.mute();
		}
	}
}

