function getSineWave(x, f) {
	var frequency = (f == undefined) ? 1 : f;
	return Math.sin(2 * Math.PI * x * frequency);
}

function getSquareWave(x, f) {
	var frequency = (f == undefined) ? 1 : f;
	return Math.sign(getSineWave(x, f));
}

function getSawtoothWave(x, f) {
	return (x - Math.floor(x + 1/2)) * 2;
}

function getTriangleWave(x) {
	return (Math.abs(getSawtoothWave(x)) * 2 ) - 1;
}

Math.sign = function(x) {
	if(x == 0) {
		return 0;
	} else if(x > 0) {
		return 1;
	}
	return -1;
}


